                          id="enhanceCTA"
                          checked={bulkAdsForm.massApplyOptions.advantagePlusEnhancements.enhanceCTA}
                          onChange={(e) => setBulkAdsForm(prev => ({
                            ...prev,
                            massApplyOptions: {
                              ...prev.massApplyOptions,
                              advantagePlusEnhancements: {
                                ...prev.massApplyOptions.advantagePlusEnhancements,
                                enhanceCTA: e.target.checked
                              }
                            }
                          }))}
                          className="mt-1"
                        />
                        <Label htmlFor="enhanceCTA">Enhance CTA</Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id="addVideoEffects"
                          checked={bulkAdsForm.massApplyOptions.advantagePlusEnhancements.addVideoEffects}
                          onChange={(e) => setBulkAdsForm(prev => ({
                            ...prev,
                            massApplyOptions: {
                              ...prev.massApplyOptions,
                              advantagePlusEnhancements: {
                                ...prev.massApplyOptions.advantagePlusEnhancements,
                                addVideoEffects: e.target.checked
                              }
                            }
                          }))}
                          className="mt-1"
                        />
                        <Label htmlFor="addVideoEffects">Add video effects</Label>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <Label className="text-xs">Mass Apply Pixel</Label>
                      <select
                        value={bulkAdsForm.massApplyOptions.pixelId || ''}
                        onChange={(e) => setBulkAdsForm(prev => ({
                          ...prev,
                          massApplyOptions: { ...prev.massApplyOptions, pixelId: e.target.value }
                        }))}
                        className="w-full text-sm border rounded px-2 py-1"
                      >
                        <option value="">Select Pixel</option>
                        {mockPixels.map((pixel) => (
                          <option key={pixel.id} value={pixel.id}>
                            {pixel.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="space-y-2">
                      <Label className="text-xs">Mass Apply URL Params</Label>
                      <Input
                        value={bulkAdsForm.massApplyOptions.urlParams || ''}
                        onChange={(e) => setBulkAdsForm(prev => ({
                          ...prev,
                          massApplyOptions: { ...prev.massApplyOptions, urlParams: e.target.value }
                        }))}
                        placeholder="key1=value1&key2=value2"
                        className="text-sm"
                      />
                    </div>
                  </div>

                  {/* Individual Ad Rows */}
                  <div className="space-y-2">
                    {bulkAdsForm.adItems.map((item) => (
                      <AdRow
                        key={item.id}
                        ad={item}
                        campaigns={bulkAdsForm.campaigns}
                        facebookPages={bulkAdsForm.facebookPages}
                        instagramPages={bulkAdsForm.instagramPages}
                        pixels={mockPixels}
                        onUpdate={(updatedAd) => {
                          setBulkAdsForm(prev => ({
                            ...prev,
                            adItems: prev.adItems.map(item => 
                              item.id === updatedAd.id ? updatedAd : item
                            )
                          }));
                        }}
                        onRemove={() => {
                          setBulkAdsForm(prev => ({
                            ...prev,
                            adItems: prev.adItems.filter(item => item.id !== item.id),
                            uploadedMedia: prev.uploadedMedia.filter(media => 
                              !prev.adItems.find(item => item.id === item.id)?.mediaId.includes(media.id)
                            )
                          }));
                        }}
                      />
                    ))}
                  </div>
                </div>
              )}

              <Button 
                onClick={handleCreateBulkAds}
                disabled={createEnhancedBulkAds.isPending || !bulkAdsForm.templateId || bulkAdsForm.adItems.length === 0}
                className="w-full"
              >
                {createEnhancedBulkAds.isPending ? 'Creating Ads...' : 'Create Bulk Ads'}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Jobs Tab */}
        <TabsContent value="jobs" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Job Status</CardTitle>
              <CardDescription>
                Monitor the progress of your bulk ad creation jobs
              </CardDescription>
            </CardHeader>
            <CardContent>
              {jobs.isLoading ? (
                <div>Loading jobs...</div>
              ) : jobs.data ? (
                <div className="space-y-4">
                  {jobs.data.jobs.map((job) => (
                    <div key={job.id} className="border rounded p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold">Job {job.id.slice(0, 8)}</h3>
                          <p className="text-sm text-muted-foreground">
                            {job.createdAds} created, {job.failedAds} failed
                          </p>
                        </div>
                        <Badge variant={
                          job.status === 'COMPLETED' ? 'default' :
                          job.status === 'FAILED' ? 'destructive' :
                          'secondary'
                        }>
                          {job.status}
                        </Badge>
                      </div>
                      <Progress value={job.progress} className="mt-2" />
                    </div>
                  ))}
                </div>
              ) : (
                <div>No jobs found</div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

export function Dashboard() {
  return (
    <QueryClientProvider client={queryClient}>
      <DashboardContent />
      <Toaster />
    </QueryClientProvider>
  );
}
