import React, { useState } from 'react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { Toaster } from '@/components/ui/toaster';
import { useToast } from '@/hooks/use-toast';
import { 
  useAuthStatus, 
  useValidateCredentials, 
  useImages, 
  useUploadImages,
  useTemplates,
  useJobs,
  useCreateBulkAds
} from '@/hooks/useApi';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { FacebookCredentials, type AdTemplate } from '@/lib/api';

// Create a client
const queryClient = new QueryClient();

function DashboardContent() {
  const { toast } = useToast();
  const [credentials, setCredentials] = useState<FacebookCredentials>({
    appId: '',
    appSecret: '',
    accessToken: '',
    adAccountId: '',
  });

  // API Hooks
  const authStatus = useAuthStatus();
  const validateCredentials = useValidateCredentials();
  const images = useImages();
  const uploadImages = useUploadImages();
  const templates = useTemplates();
  const jobs = useJobs();
  const createBulkAds = useCreateBulkAds();

  // File upload handling
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    if (files.length === 0) return;

    try {
      await uploadImages.mutateAsync(files);
      toast({
        title: 'Images uploaded successfully',
        description: `Uploaded ${files.length} images`,
      });
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  // Credential validation
  const handleValidateCredentials = async () => {
    try {
      await validateCredentials.mutateAsync(credentials);
      toast({
        title: 'Credentials validated',
        description: 'Facebook API credentials are valid',
      });
    } catch (error) {
      toast({
        title: 'Validation failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  // Bulk ad creation
  const handleCreateBulkAds = async (templateId: string, imageIds: string[]) => {
    try {
      await createBulkAds.mutateAsync({
        templateId,
        images: imageIds,
        campaignName: 'Bulk Campaign',
        adSetName: 'Bulk Ad Set',
        options: {
          createCampaign: true,
          createAdSet: true,
          status: 'PAUSED',
        },
      });
      toast({
        title: 'Bulk ad creation started',
        description: 'Your ads are being created in the background',
      });
    } catch (error) {
      toast({
        title: 'Bulk ad creation failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Facebook Ads Bulk Uploader</h1>
        <Badge variant={authStatus.data?.isInitialized ? 'default' : 'secondary'}>
          {authStatus.data?.isInitialized ? 'Connected' : 'Disconnected'}
        </Badge>
      </div>

      <Tabs defaultValue="auth" className="space-y-4">
        <TabsList>
          <TabsTrigger value="auth">Authentication</TabsTrigger>
          <TabsTrigger value="images">Images</TabsTrigger>
          <TabsTrigger value="templates">Templates</TabsTrigger>
          <TabsTrigger value="ads">Bulk Ads</TabsTrigger>
          <TabsTrigger value="jobs">Jobs</TabsTrigger>
        </TabsList>

        {/* Authentication Tab */}
        <TabsContent value="auth" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Facebook API Credentials</CardTitle>
              <CardDescription>
                Enter your Facebook App credentials to connect to the Facebook Marketing API
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="appId">App ID</Label>
                  <Input
                    id="appId"
                    value={credentials.appId}
                    onChange={(e) => setCredentials({ ...credentials, appId: e.target.value })}
                    placeholder="Enter your Facebook App ID"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="appSecret">App Secret</Label>
                  <Input
                    id="appSecret"
                    type="password"
                    value={credentials.appSecret}
                    onChange={(e) => setCredentials({ ...credentials, appSecret: e.target.value })}
                    placeholder="Enter your Facebook App Secret"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="accessToken">Access Token</Label>
                  <Input
                    id="accessToken"
                    type="password"
                    value={credentials.accessToken}
                    onChange={(e) => setCredentials({ ...credentials, accessToken: e.target.value })}
                    placeholder="Enter your Facebook Access Token"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="adAccountId">Ad Account ID</Label>
                  <Input
                    id="adAccountId"
                    value={credentials.adAccountId}
                    onChange={(e) => setCredentials({ ...credentials, adAccountId: e.target.value })}
                    placeholder="act_123456789"
                  />
                </div>
              </div>
              <Button 
                onClick={handleValidateCredentials}
                disabled={validateCredentials.isPending}
                className="w-full"
              >
                {validateCredentials.isPending ? 'Validating...' : 'Validate Credentials'}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Images Tab */}
        <TabsContent value="images" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Upload Images</CardTitle>
              <CardDescription>
                Upload images that will be used for your Facebook ads
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="images">Select Images</Label>
                <Input
                  id="images"
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleFileUpload}
                  disabled={uploadImages.isPending}
                />
              </div>
              {uploadImages.isPending && (
                <Progress value={33} className="w-full" />
              )}
            </CardContent>
          </Card>

          {images.data && (
            <Card>
              <CardHeader>
                <CardTitle>Uploaded Images ({images.data.total})</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  {images.data.images.map((image) => (
                    <div key={image.id} className="space-y-2">
                      <img
                        src={image.url}
                        alt={image.originalName}
                        className="w-full h-32 object-cover rounded"
                      />
                      <p className="text-sm text-muted-foreground truncate">
                        {image.originalName}
                      </p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        {/* Templates Tab */}
        <TabsContent value="templates" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Ad Templates</CardTitle>
              <CardDescription>
                Create and manage ad templates for bulk ad creation
              </CardDescription>
            </CardHeader>
            <CardContent>
              {templates.isLoading ? (
                <div>Loading templates...</div>
              ) : templates.data ? (
                <div className="space-y-4">
                  {templates.data.templates.map((template) => (
                    <div key={template.id} className="border rounded p-4">
                      <h3 className="font-semibold">{template.name}</h3>
                      <p className="text-sm text-muted-foreground">{template.description}</p>
                      <div className="mt-2">
                        <Badge variant="outline">{template.budget.currency}</Badge>
                        <Badge variant="outline">{template.budget.type}</Badge>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div>No templates found</div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Bulk Ads Tab */}
        <TabsContent value="ads" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Create Bulk Ads</CardTitle>
              <CardDescription>
                Create multiple ads using templates and uploaded images
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {templates.data && images.data && (
                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label>Select Template</Label>
                    <Select>
                      <SelectTrigger>
                        <SelectValue placeholder="Choose a template" />
                      </SelectTrigger>
                      <SelectContent>
                        {templates.data.templates.map((template) => (
                          <SelectItem key={template.id} value={template.id}>
                            {template.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="space-y-2">
                    <Label>Select Images</Label>
                    <div className="grid grid-cols-4 gap-2">
                      {images.data.images.map((image) => (
                        <div key={image.id} className="relative">
                          <img
                            src={image.url}
                            alt={image.originalName}
                            className="w-full h-20 object-cover rounded cursor-pointer border-2 border-transparent hover:border-primary"
                          />
                        </div>
                      ))}
                    </div>
                  </div>

                  <Button 
                    onClick={() => {
                      if (templates.data?.templates[0] && images.data?.images.length > 0) {
                        handleCreateBulkAds(
                          templates.data.templates[0].id,
                          images.data.images.map(img => img.id)
                        );
                      }
                    }}
                    disabled={createBulkAds.isPending}
                    className="w-full"
                  >
                    {createBulkAds.isPending ? 'Creating Ads...' : 'Create Bulk Ads'}
                  </Button>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Jobs Tab */}
        <TabsContent value="jobs" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Job Status</CardTitle>
              <CardDescription>
                Monitor the progress of your bulk ad creation jobs
              </CardDescription>
            </CardHeader>
            <CardContent>
              {jobs.isLoading ? (
                <div>Loading jobs...</div>
              ) : jobs.data ? (
                <div className="space-y-4">
                  {jobs.data.jobs.map((job) => (
                    <div key={job.id} className="border rounded p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold">Job {job.id.slice(0, 8)}</h3>
                          <p className="text-sm text-muted-foreground">
                            {job.createdAds} created, {job.failedAds} failed
                          </p>
                        </div>
                        <Badge variant={
                          job.status === 'COMPLETED' ? 'default' :
                          job.status === 'FAILED' ? 'destructive' :
                          'secondary'
                        }>
                          {job.status}
                        </Badge>
                      </div>
                      <Progress value={job.progress} className="mt-2" />
                    </div>
                  ))}
                </div>
              ) : (
                <div>No jobs found</div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

export function Dashboard() {
  return (
    <QueryClientProvider client={queryClient}>
      <DashboardContent />
      <Toaster />
    </QueryClientProvider>
  );
}
