import React, { useState } from 'react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { 
  useAuthStatus, 
  useValidateCredentials, 
  useMedia, 
  useUploadMedia,
  useTemplates,
  useCreateTemplate,
  useJobs,
  useCreateBulkAds
} from '../hooks/useApi';
import { Button } from './ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Progress } from './ui/progress';
import { Badge } from './ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { Toaster } from './ui/toaster';
import { useToast } from '../hooks/use-toast';
import { FacebookCredentials } from '../lib/api';
import { BulkAdItem, FacebookCampaign, FacebookPage, MassApplyOptions } from '@/types';
import { AdRow } from './AdRow';

// Create a client
const queryClient = new QueryClient();

function DashboardContent() {
  const { toast } = useToast();
  const [credentials, setCredentials] = useState<FacebookCredentials>({
    appId: '',
    appSecret: '',
    accessToken: '',
    adAccountId: '',
  });

  // Template form state
  const [templateForm, setTemplateForm] = useState({
    name: '',
    description: '',
    adCopy: {
      headline: '',
      primaryText: '',
      callToAction: 'LEARN_MORE' as const,
      description: ''
    },
    budget: {
      amount: '',
      currency: 'USD' as 'USD' | 'EUR' | 'GBP' | 'CAD',
      type: 'DAILY' as 'DAILY' | 'LIFETIME'
    },
    targeting: {
      ageMin: 18,
      ageMax: 65,
      locations: [] as string[],
      interests: [] as string[]
    },
    placement: {
      facebook: true,
      instagram: true,
      audienceNetwork: false
    }
  });

  // Enhanced bulk ads form state
  const [bulkAdsForm, setBulkAdsForm] = useState({
    templateId: '',
    uploadedMedia: [] as any[], // Store uploaded media for this session
    adItems: [] as BulkAdItem[], // Individual ad items
    campaigns: [] as FacebookCampaign[],
    facebookPages: [] as FacebookPage[],
    massApplyOptions: {} as MassApplyOptions
  });

  // API Hooks
  const authStatus = useAuthStatus();
  const validateCredentials = useValidateCredentials();
  const uploadMedia = useUploadMedia();
  const templates = useTemplates();
  const createTemplate = useCreateTemplate();
  const jobs = useJobs();
  const createBulkAds = useCreateBulkAds();

  // Media upload handling for bulk ads session
  const handleMediaUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);
    if (files.length === 0) return;

    try {
      const result = await uploadMedia.mutateAsync(files);

      // Add uploaded media to the current session
      setBulkAdsForm(prev => ({
        ...prev,
        uploadedMedia: [...prev.uploadedMedia, ...result.uploadedMedia]
      }));

      toast({
        title: 'Media uploaded successfully',
        description: `Uploaded ${result.totalUploaded} files for this bulk ad session`,
      });
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  // Remove media from session
  const handleRemoveMedia = (mediaId: string) => {
    setBulkAdsForm(prev => ({
      ...prev,
      uploadedMedia: prev.uploadedMedia.filter(media => media.id !== mediaId)
    }));
  };

  // Credential validation
  const handleValidateCredentials = async () => {
    try {
      await validateCredentials.mutateAsync(credentials);
      toast({
        title: 'Credentials validated',
        description: 'Facebook API credentials are valid',
      });
    } catch (error) {
      toast({
        title: 'Validation failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  // Template creation
  const handleCreateTemplate = async () => {
    // Comprehensive validation matching backend requirements
    const errors: string[] = [];

    // Validate template name
    if (!templateForm.name.trim()) {
      errors.push('Template name is required');
    } else if (templateForm.name.trim().length > 100) {
      errors.push('Template name must be less than 100 characters');
    }

    // Validate headline
    if (!templateForm.adCopy.headline.trim()) {
      errors.push('Ad headline is required');
    } else if (templateForm.adCopy.headline.trim().length > 40) {
      errors.push('Headline must be less than 40 characters');
    }

    // Validate primary text
    if (!templateForm.adCopy.primaryText.trim()) {
      errors.push('Primary text is required');
    } else if (templateForm.adCopy.primaryText.trim().length > 125) {
      errors.push('Primary text must be less than 125 characters');
    }

    // Validate description
    if (templateForm.description.trim().length > 500) {
      errors.push('Description must be less than 500 characters');
    }

    // Validate budget amount
    let budgetAmount: number = 0;
    if (!templateForm.budget.amount.trim()) {
      errors.push('Budget amount is required');
    } else {
      budgetAmount = parseFloat(templateForm.budget.amount);
      if (isNaN(budgetAmount) || budgetAmount <= 0) {
        errors.push('Please enter a valid budget amount (greater than 0)');
      }
    }

    // Show all validation errors
    if (errors.length > 0) {
      toast({
        title: 'Template creation failed',
        description: errors.join(', '),
        variant: 'destructive',
      });
      return;
    }

    // Ensure budgetAmount is valid
    if (budgetAmount <= 0) {
      budgetAmount = parseFloat(templateForm.budget.amount);
    }

    try {
      const templateData = {
        name: templateForm.name.trim(),
        ...(templateForm.description.trim() && { description: templateForm.description.trim() }),
        adCopy: {
          headline: templateForm.adCopy.headline.trim(),
          primaryText: templateForm.adCopy.primaryText.trim(),
          callToAction: templateForm.adCopy.callToAction,
          ...(templateForm.adCopy.description.trim() && { description: templateForm.adCopy.description.trim() })
        },
        budget: {
          amount: budgetAmount,
          currency: templateForm.budget.currency,
          type: templateForm.budget.type
        },
        targeting: templateForm.targeting,
        placement: templateForm.placement
      };

      await createTemplate.mutateAsync(templateData);

      toast({
        title: 'Template created successfully',
        description: 'Your ad template has been saved',
      });

      // Reset form
      setTemplateForm({
        name: '',
        description: '',
        adCopy: {
          headline: '',
          primaryText: '',
          callToAction: 'LEARN_MORE',
          description: ''
        },
        budget: {
          amount: '',
          currency: 'USD',
          type: 'DAILY'
        },
        targeting: {
          ageMin: 18,
          ageMax: 65,
          locations: [],
          interests: []
        },
        placement: {
          facebook: true,
          instagram: true,
          audienceNetwork: false
        }
      });
    } catch (error) {
      toast({
        title: 'Template creation failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  // Bulk ad creation
  const handleCreateBulkAds = async () => {
    if (!bulkAdsForm.templateId || bulkAdsForm.uploadedMedia.length === 0) {
      toast({
        title: 'Missing required fields',
        description: 'Please select a template and upload media files',
        variant: 'destructive',
      });
      return;
    }

    try {
      await createBulkAds.mutateAsync({
        templateId: bulkAdsForm.templateId,
        media: bulkAdsForm.uploadedMedia.map(media => media.id),
        campaignName: bulkAdsForm.campaignName,
        adSetName: bulkAdsForm.adSetName,
        options: {
          createCampaign: bulkAdsForm.createCampaign,
          createAdSet: bulkAdsForm.createAdSet,
          status: bulkAdsForm.status,
          campaignBudget: bulkAdsForm.campaignBudget ? parseFloat(bulkAdsForm.campaignBudget) : undefined,
          adSetBudget: bulkAdsForm.adSetBudget ? parseFloat(bulkAdsForm.adSetBudget) : undefined,
        },
      });
      toast({
        title: 'Bulk ad creation started',
        description: 'Your ads are being created in the background',
      });

      // Reset form and clear uploaded media
      setBulkAdsForm({
        templateId: '',
        uploadedMedia: [],
        campaignName: '',
        adSetName: '',
        createCampaign: true,
        createAdSet: true,
        status: 'PAUSED',
        campaignBudget: '',
        adSetBudget: ''
      });
    } catch (error) {
      toast({
        title: 'Bulk ad creation failed',
        description: error instanceof Error ? error.message : 'Unknown error',
        variant: 'destructive',
      });
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Facebook Ads Bulk Uploader</h1>
        <Badge variant={authStatus.data?.isInitialized ? 'default' : 'secondary'}>
          {authStatus.data?.isInitialized ? 'Connected' : 'Disconnected'}
        </Badge>
      </div>

      <Tabs defaultValue="auth" className="space-y-4">
        <TabsList>
          <TabsTrigger value="auth">Authentication</TabsTrigger>
          <TabsTrigger value="templates">Templates</TabsTrigger>
          <TabsTrigger value="ads">Bulk Ads</TabsTrigger>
          <TabsTrigger value="jobs">Jobs</TabsTrigger>
        </TabsList>

        {/* Authentication Tab */}
        <TabsContent value="auth" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Facebook API Credentials</CardTitle>
              <CardDescription>
                Enter your Facebook App credentials to connect to the Facebook Marketing API
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="appId">App ID</Label>
                  <Input
                    id="appId"
                    value={credentials.appId}
                    onChange={(e) => setCredentials({ ...credentials, appId: e.target.value })}
                    placeholder="Enter your Facebook App ID"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="appSecret">App Secret</Label>
                  <Input
                    id="appSecret"
                    type="password"
                    value={credentials.appSecret}
                    onChange={(e) => setCredentials({ ...credentials, appSecret: e.target.value })}
                    placeholder="Enter your Facebook App Secret"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="accessToken">Access Token</Label>
                  <Input
                    id="accessToken"
                    type="password"
                    value={credentials.accessToken}
                    onChange={(e) => setCredentials({ ...credentials, accessToken: e.target.value })}
                    placeholder="Enter your Facebook Access Token"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="adAccountId">Ad Account ID</Label>
                  <Input
                    id="adAccountId"
                    value={credentials.adAccountId}
                    onChange={(e) => setCredentials({ ...credentials, adAccountId: e.target.value })}
                    placeholder="act_123456789"
                  />
                </div>
              </div>
              <Button 
                onClick={handleValidateCredentials}
                disabled={validateCredentials.isPending}
                className="w-full"
              >
                {validateCredentials.isPending ? 'Validating...' : 'Validate Credentials'}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Templates Tab */}
        <TabsContent value="templates" className="space-y-4">
          {/* Create Template Form */}
          <Card>
            <CardHeader>
              <CardTitle>Create New Template</CardTitle>
              <CardDescription>
                Create a new ad template for bulk ad creation
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="templateName">Template Name</Label>
                  <Input
                    id="templateName"
                    value={templateForm.name}
                    onChange={(e) => setTemplateForm({ ...templateForm, name: e.target.value })}
                    placeholder="Enter template name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="callToAction">Call to Action</Label>
                  <Select 
                    value={templateForm.adCopy.callToAction}
                    onValueChange={(value) => setTemplateForm({ 
                      ...templateForm, 
                      adCopy: { ...templateForm.adCopy, callToAction: value as any }
                    })}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select call to action" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="SHOP_NOW">Shop Now</SelectItem>
                      <SelectItem value="LEARN_MORE">Learn More</SelectItem>
                      <SelectItem value="SIGN_UP">Sign Up</SelectItem>
                      <SelectItem value="BOOK_NOW">Book Now</SelectItem>
                      <SelectItem value="CONTACT_US">Contact Us</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="templateDescription">Description</Label>
                <Input
                  id="templateDescription"
                  value={templateForm.description}
                  onChange={(e) => setTemplateForm({ ...templateForm, description: e.target.value })}
                  placeholder="Enter template description"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="headline">Ad Headline</Label>
                  <Input
                    id="headline"
                    value={templateForm.adCopy.headline}
                    onChange={(e) => setTemplateForm({ 
                      ...templateForm, 
                      adCopy: { ...templateForm.adCopy, headline: e.target.value }
                    })}
                    placeholder="Enter ad headline"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="primaryText">Primary Text</Label>
                  <Input
                    id="primaryText"
                    value={templateForm.adCopy.primaryText}
                    onChange={(e) => setTemplateForm({ 
                      ...templateForm, 
                      adCopy: { ...templateForm.adCopy, primaryText: e.target.value }
                    })}
                    placeholder="Enter primary text"
                  />
                </div>
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="budgetAmount">Budget Amount</Label>
                  <Input
                    id="budgetAmount"
                    type="number"
                    value={templateForm.budget.amount}
                    onChange={(e) => setTemplateForm({ 
                      ...templateForm, 
                      budget: { ...templateForm.budget, amount: e.target.value }
                    })}
                    placeholder="0.00"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="budgetCurrency">Currency</Label>
                  <Select 
                    value={templateForm.budget.currency}
                    onValueChange={(value) => setTemplateForm({ 
                      ...templateForm, 
                      budget: { ...templateForm.budget, currency: value as 'USD' | 'EUR' | 'GBP' | 'CAD' }
                    })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="USD">USD</SelectItem>
                      <SelectItem value="EUR">EUR</SelectItem>
                      <SelectItem value="GBP">GBP</SelectItem>
                      <SelectItem value="CAD">CAD</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="budgetType">Budget Type</Label>
                  <Select 
                    value={templateForm.budget.type}
                    onValueChange={(value) => setTemplateForm({ 
                      ...templateForm, 
                      budget: { ...templateForm.budget, type: value as 'DAILY' | 'LIFETIME' }
                    })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="DAILY">Daily</SelectItem>
                      <SelectItem value="LIFETIME">Lifetime</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <Button 
                onClick={handleCreateTemplate}
                disabled={
                  createTemplate.isPending || 
                  !templateForm.name.trim() || 
                  !templateForm.adCopy.headline.trim() || 
                  !templateForm.adCopy.primaryText.trim() ||
                  !templateForm.budget.amount.trim() ||
                  isNaN(parseFloat(templateForm.budget.amount)) ||
                  parseFloat(templateForm.budget.amount) <= 0
                }
                className="w-full"
              >
                {createTemplate.isPending ? 'Creating Template...' : 'Create Template'}
              </Button>
            </CardContent>
          </Card>

          {/* Existing Templates */}
          <Card>
            <CardHeader>
              <CardTitle>Existing Templates</CardTitle>
              <CardDescription>
                Your saved ad templates
              </CardDescription>
            </CardHeader>
            <CardContent>
              {templates.isLoading ? (
                <div>Loading templates...</div>
              ) : templates.data && templates.data.templates.length > 0 ? (
                <div className="space-y-4">
                  {templates.data.templates.map((template) => (
                    <div key={template.id} className="border rounded p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold">{template.name}</h3>
                          <p className="text-sm text-muted-foreground">{template.description}</p>
                        </div>
                        <Badge variant="outline">{template.adCopy.callToAction}</Badge>
                      </div>
                      <div className="mt-2 flex gap-2">
                        <Badge variant="outline">{template.budget.currency}</Badge>
                        <Badge variant="outline">{template.budget.type}</Badge>
                        <Badge variant="outline">{template.budget.amount}</Badge>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center text-muted-foreground py-8">
                  No templates found. Create your first template above.
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Bulk Ads Tab */}
        <TabsContent value="ads" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Create Bulk Ads</CardTitle>
              <CardDescription>
                Upload media and create multiple ads using templates
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Media Upload Section */}
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <Label className="text-lg font-semibold">Upload Media for This Session</Label>
                  <Badge variant="outline">{bulkAdsForm.uploadedMedia.length} files</Badge>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="media">Select Images & Videos</Label>
                  <Input
                    id="media"
                    type="file"
                    multiple
                    accept="image/*,video/*"
                    onChange={handleMediaUpload}
                    disabled={uploadMedia.isPending}
                  />
                  <p className="text-sm text-muted-foreground">
                    Supported formats: JPG, PNG, GIF, MP4, MOV, AVI
                  </p>
                </div>
                {uploadMedia.isPending && (
                  <Progress value={33} className="w-full" />
                )}
              </div>

              {/* Uploaded Media Preview */}
              {bulkAdsForm.uploadedMedia.length > 0 && (
                <div className="space-y-2">
                  <Label>Uploaded Media for This Session</Label>
                  <div className="grid grid-cols-4 gap-2 max-h-64 overflow-y-auto border rounded p-4">
                    {bulkAdsForm.uploadedMedia.map((item) => (
                      <div key={item.id} className="relative group">
                        {item.mediaType === 'image' ? (
                          <img
                            src={item.url}
                            alt={item.originalName}
                            className="w-full h-20 object-cover rounded"
                          />
                        ) : (
                          <video
                            src={item.url}
                            className="w-full h-20 object-cover rounded"
                            muted
                          />
                        )}
                        <div className="absolute top-1 right-1">
                          <Badge variant="outline" className="text-xs">
                            {item.mediaType}
                          </Badge>
                        </div>
                        <button
                          onClick={() => handleRemoveMedia(item.id)}
                          className="absolute top-1 left-1 bg-red-500 text-white rounded-full w-5 h-5 text-xs opacity-0 group-hover:opacity-100 transition-opacity"
                        >

                        </button>
                        <p className="text-xs text-muted-foreground truncate mt-1">
                          {item.originalName}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Template Selection */}
              <div className="space-y-2">
                <Label>Select Template</Label>
                <Select 
                  value={bulkAdsForm.templateId}
                  onValueChange={(value) => setBulkAdsForm({ ...bulkAdsForm, templateId: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Choose a template" />
                  </SelectTrigger>
                  <SelectContent>
                    {templates.data?.templates.map((template) => (
                      <SelectItem key={template.id} value={template.id}>
                        {template.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Campaign Settings */}
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="campaignName">Campaign Name</Label>
                  <Input
                    id="campaignName"
                    value={bulkAdsForm.campaignName}
                    onChange={(e) => setBulkAdsForm({ ...bulkAdsForm, campaignName: e.target.value })}
                    placeholder="Enter campaign name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="adSetName">Ad Set Name</Label>
                  <Input
                    id="adSetName"
                    value={bulkAdsForm.adSetName}
                    onChange={(e) => setBulkAdsForm({ ...bulkAdsForm, adSetName: e.target.value })}
                    placeholder="Enter ad set name"
                  />
                </div>
              </div>

              {/* Budget Settings */}
              <div className="grid grid-cols-3 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="campaignBudget">Campaign Budget (Optional)</Label>
                  <Input
                    id="campaignBudget"
                    type="number"
                    value={bulkAdsForm.campaignBudget}
                    onChange={(e) => setBulkAdsForm({ ...bulkAdsForm, campaignBudget: e.target.value })}
                    placeholder="0.00"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="adSetBudget">Ad Set Budget (Optional)</Label>
                  <Input
                    id="adSetBudget"
                    type="number"
                    value={bulkAdsForm.adSetBudget}
                    onChange={(e) => setBulkAdsForm({ ...bulkAdsForm, adSetBudget: e.target.value })}
                    placeholder="0.00"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="status">Status</Label>
                  <Select 
                    value={bulkAdsForm.status}
                    onValueChange={(value) => setBulkAdsForm({ 
                      ...bulkAdsForm, 
                      status: value as 'ACTIVE' | 'PAUSED'
                    })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="PAUSED">Paused</SelectItem>
                      <SelectItem value="ACTIVE">Active</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <Button 
                onClick={handleCreateBulkAds}
                disabled={createBulkAds.isPending || !bulkAdsForm.templateId || bulkAdsForm.uploadedMedia.length === 0}
                className="w-full"
              >
                {createBulkAds.isPending ? 'Creating Ads...' : 'Create Bulk Ads'}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Jobs Tab */}
        <TabsContent value="jobs" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Job Status</CardTitle>
              <CardDescription>
                Monitor the progress of your bulk ad creation jobs
              </CardDescription>
            </CardHeader>
            <CardContent>
              {jobs.isLoading ? (
                <div>Loading jobs...</div>
              ) : jobs.data ? (
                <div className="space-y-4">
                  {jobs.data.jobs.map((job) => (
                    <div key={job.id} className="border rounded p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold">Job {job.id.slice(0, 8)}</h3>
                          <p className="text-sm text-muted-foreground">
                            {job.createdAds} created, {job.failedAds} failed
                          </p>
                        </div>
                        <Badge variant={
                          job.status === 'COMPLETED' ? 'default' :
                          job.status === 'FAILED' ? 'destructive' :
                          'secondary'
                        }>
                          {job.status}
                        </Badge>
                      </div>
                      <Progress value={job.progress} className="mt-2" />
                    </div>
                  ))}
                </div>
              ) : (
                <div>No jobs found</div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

export function Dashboard() {
  return (
    <QueryClientProvider client={queryClient}>
      <DashboardContent />
      <Toaster />
    </QueryClientProvider>
  );
}
